window.addEventListener("DOMContentLoaded",function(){var form=document.getElementById("contact-form");var button=document.getElementById("contact-form-button");var status=document.getElementById("contact-form-status");function success(){form.reset();button.style="display: none ";status.innerHTML="Thanks! Contact form is submitted successfully.";}
function error(){status.innerHTML="Dziękujemy - kartka została wysłana - czeka na weryfikację";form.reset()}
form.addEventListener("submit",function(ev){ev.preventDefault();var data=new FormData(form);console.log(data);ajax(form.method,form.action,data,success,error);});});function ajax(method,url,data,success,error){var xhr=new XMLHttpRequest();xhr.open('POST','https://docs.google.com/forms/d/e/1FAIpQLSfLvT42ejmsIrZL5v9IJL7eJp8Cs7y7sQdOhWcjXRqr9R3JnQ/formResponse');xhr.onreadystatechange=function(){if(xhr.readyState!==XMLHttpRequest.DONE)return;if(xhr.status===200){success(xhr.response,xhr.responseType);}else{error(xhr.status,xhr.response,xhr.responseType);}};xhr.send(data);}